import React, { useState, useEffect } from "react";
import ComplaintDetail from './ComplaintDetail';
import MediaPopup from './MediaPopup'; 
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

function StatisticsTable() {
    const [data, setData] = useState([]);
    const [selectedComplaint, setSelectedComplaint] = useState(null);
    const [selectedMedia, setSelectedMedia] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [statusModalVisible, setStatusModalVisible] = useState(false);
    const [selectedStatus, setSelectedStatus] = useState("");
    const [currentComplaintId, setCurrentComplaintId] = useState(null);

    const navigate = useNavigate(); // Get the navigate function

    useEffect(() => {
        const fetchComplaints = async () => {
            try {
                // Get the token from localStorage
                const token = localStorage.getItem('authToken');

                if (!token) {
                    navigate('/login'); // Redirect to login page if no token
                    return;
                }

                const response = await fetch('http://127.0.0.1:8000/get_complaints/', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const complaints = await response.json();
                    setData(complaints);
                } else if (response.status === 401) {
                    setErrorMessage('Unauthorized access. Please log in.');
                    navigate('/login'); // Redirect to login page if unauthorized
                } else {
                    throw new Error('Error fetching complaints.');
                }
            } catch (error) {
                console.error('Error fetching complaints:', error);
                setErrorMessage('Error fetching complaints. Please try again.');
            }
        };

        fetchComplaints();
    }, [navigate]);

    const handleRowClick = (complaint) => {
        setSelectedComplaint(complaint);  // Set the clicked complaint as the selected one
    };

    const handleCloseDetail = () => {
        setSelectedComplaint(null);  // Close the detail view
    };

    const handleShowMedia = (image) => {
        setSelectedMedia(image);  // Show the media in a popup
    };

    const handleCloseMedia = () => {
        setSelectedMedia(null);  // Close the media popup
    };

    const renderComplaintText = (text) => {
        if (typeof text !== 'string') {
            return 'No description available'; // Return a fallback string if text is not a string
        }
        
        return text.length > 20 ? text.substring(0, 20) + '...' : text;
    };
    const handleStatusChange = () => {
        setData((prevData) =>
            prevData.map((complaint) =>
                complaint.id === currentComplaintId ? { ...complaint, status: selectedStatus } : complaint
            )
        );
        setStatusModalVisible(false);
        setCurrentComplaintId(null);
        setSelectedStatus("");
    };

    const openStatusModal = (id, currentStatus) => {
        setCurrentComplaintId(id);
        setSelectedStatus(currentStatus);
        setStatusModalVisible(true);
    };

    return (
        <>
            {errorMessage && <p className="text-danger text-center mt-3">{errorMessage}</p>}
            <div className="table-responsive-sm" style={{ overflowX: 'auto', overflowY: 'auto', maxHeight: '250px', maxWidth: '100%' }}>
                <table className="table table-hover">
                    <thead>
                        <tr className="table-success">
                            <th>SL NO</th>
                            <th>PNR</th>
                            <th>Customer's Complaint</th>
                            <th>Category</th>
                            {/* <th>Sub-Category</th>
                            <th>Priority</th> */}
                            <th>Status</th>
                            <th>Media</th>
                            <th>Action</th>
                                
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((complaint, index) => (
                            <tr key={index} onClick={() => handleRowClick(complaint)} style={{ cursor: 'pointer' }}>
                                <td>{index + 1}</td>
                                <td>{complaint.pnr}</td>
                                <td>{renderComplaintText(complaint.description)}</td>
                                <td>{complaint.category}</td>
                                {/* <td>{complaint.sub_category}</td>
                                <td>{complaint.priority}</td> */}
                                <td>{complaint.status}</td>
                                <td>
                                    <button onClick={(e) => { e.stopPropagation(); handleShowMedia(complaint.image); }} className="btn btn-link">
                                        Show Media
                                    </button>
                                </td>
                                <td>
                                    <button onClick={(e) => { e.stopPropagation(); openStatusModal(complaint.id, complaint.status); }} className="btn btn-success">
                                        Change Status
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Render the complaint detail view when a complaint is selected */}
            {selectedComplaint && (
                <ComplaintDetail complaint={selectedComplaint} onClose={handleCloseDetail} />
            )}

            {/* Render the media popup when an image is selected */}
            {selectedMedia && (
                <MediaPopup selectedMedia={selectedMedia} handleCloseMedia={handleCloseMedia} />
            )}
             {statusModalVisible && (
                <div className="modal fade show" style={{ display: 'block' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Change Status</h5>
                                <button type="button" className="close" style={{ position: 'absolute', right: '15px', top: '15px' }} onClick={() => setStatusModalVisible(false)}>
                                    &times;
                                </button>
                            </div>
                            <div className="modal-body">
                                <label htmlFor="status" style={{ marginRight: '25px' }}>Update Status:</label>
                                <select
                                    id="status"
                                    value={selectedStatus}
                                    onChange={(e) => setSelectedStatus(e.target.value)}                                    
                                >
                                    <option value="Pending">Pending</option>
                                    <option value="Completed">Completed</option>
                                </select>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-success" onClick={() => setStatusModalVisible(false)}>Cancel</button>
                                <button type="button" className="btn btn-success" onClick={handleStatusChange}>Save changes</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
            
        </>
    );
}

export default StatisticsTable;
