import React, { useState, useRef } from 'react';
import axios from 'axios';
import Notification from './Notification';  // Import the Notification component

function Complaint() {
  const [pnr, setPnr] = useState('');
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');  // New state for phone number
  const [otp, setOtp] = useState('');  // New state for OTP
  const [otpSent, setOtpSent] = useState(false);  // Track if OTP has been sent
  const [otpVerified, setOtpVerified] = useState(false);  // Track OTP verification status
  const [message, setMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);  // Track submission status
  const [showNotification, setShowNotification] = useState(false);  // Track notification visibility
  const fileInputRef = useRef(null);  // Reference to the file input field

  const handlePnrChange = (event) => {
    setPnr(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handlePhoneNumberChange = (event) => {
    setPhoneNumber(event.target.value);
  };

  const handleOtpChange = (event) => {
    setOtp(event.target.value);
  };

  // Function to send OTP
  const handleSendOtp = async () => {
    if (phoneNumber.length !== 10) {
      setMessage('Please enter a valid 10-digit phone number.');
      return;
    }

    try {
      // Simulate OTP sending - In a real scenario, you'd call an OTP API here
      console.log('Sending OTP to:', phoneNumber);
      setOtpSent(true);
      setMessage('OTP sent successfully');
    } catch (error) {
      setMessage('Error sending OTP');
      console.error(error);
    }
  };

  // Function to verify OTP
  const handleVerifyOtp = () => {
    if (otp === '1234') {  // You can replace this with actual OTP verification logic
      setOtpVerified(true);
      setMessage('OTP verified successfully');
    } else {
      setOtpVerified(false);
      setMessage('Invalid OTP. Please try again.');
    }
  };

  const handleSubmit = async () => {
    if (!pnr || !file || !description || !otpVerified) {
      setMessage('Please fill in all fields and verify OTP before submitting.');
      return;
    }

    setIsSubmitting(true);  // Disable button

    // Prepare the form data
    const formData = new FormData();
    formData.append('image', file);  // Add the image file
    formData.append('pnr', pnr);  // Add the PNR field
    formData.append('description', description);  // Add the description field

    try {
      // Post the form data to the backend
      const response = await axios.post(
        'http://127.0.0.1:8000/add_complaint_with_image/',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      // Show notification and clear form fields on success
      setMessage('');
      setShowNotification(true);  // Show notification
      setPnr('');  // Clear the PNR field
      setFile(null);  // Clear the file input
      setDescription('');  // Clear the description field
      setPhoneNumber('');  // Clear the phone number field
      setOtp('');  // Clear the OTP field
      setOtpVerified(false);  // Reset OTP verification status

      // Reset the file input
      if (fileInputRef.current) {
        fileInputRef.current.value = '';  // Reset the file input
      }

      console.log(response.data);
    } catch (error) {
      setMessage('Error submitting the complaint');
      console.error(error);
    } finally {
      setIsSubmitting(false);  // Enable button after submission is complete
    }
  };

  const handleCloseNotification = () => {
    setShowNotification(false);  // Close the notification
  };

  return (
    <div className="container d-flex justify-content-center align-items-center min-vh-100">
      <div className="card shadow" style={{ width: '400px' }}>
        <div className="card-body">
          <h2 className="card-title text-center">Complaint Enquiry</h2>
          <div className="form-group">
            <label htmlFor="pnr">Passenger Name Record (PNR):</label>
            <input
              type="text"
              className="form-control"
              id="pnr"
              value={pnr}
              onChange={handlePnrChange}
              maxLength={10}
            />
          </div>

          <div className="form-group">
            <label htmlFor="phoneNumber">Phone Number:</label>
            <input
              type="text"
              className="form-control"
              id="phoneNumber"
              value={phoneNumber}
              onChange={handlePhoneNumberChange}
              maxLength={10}
            />
            <button
              className="btn btn-primary mt-2"
              onClick={handleSendOtp}
              disabled={otpSent}
            >
              {otpSent ? 'OTP Sent' : 'Send OTP'}
            </button>
          </div>

          {otpSent && (
            <div className="form-group">
              <label htmlFor="otp">Enter OTP:</label>
              <input
                type="text"
                className="form-control"
                id="otp"
                value={otp}
                onChange={handleOtpChange}
                maxLength={4}
              />
              <button
                className="btn btn-secondary mt-2"
                onClick={handleVerifyOtp}
              >
                Verify OTP
              </button>
            </div>
          )}

          <div className="form-group">
            <label htmlFor="file">Upload File:</label>
            <input
              type="file"
              className="form-control"
              id="file"
              onChange={handleFileChange}
              ref={fileInputRef}  // Set the ref to the file input
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Complaint Description:</label>
            <textarea
              className="form-control"
              id="description"
              value={description}
              onChange={handleDescriptionChange}
              rows="3"
            ></textarea>
          </div>

          <div className="text-center mt-3">
            <button
              className="btn btn-success"
              onClick={handleSubmit}
              disabled={isSubmitting || !otpVerified}  // Disable button while submitting or if OTP isn't verified
            >
              {isSubmitting ? 'Submitting...' : 'Submit'}
            </button>
          </div>
          {message && <p className="text-center mt-2">{message}</p>}
        </div>
      </div>

      {/* Show the notification popup if form is successfully submitted */}
      {showNotification && <Notification onClose={handleCloseNotification} />}
    </div>
  );
}

export default Complaint;
