import React, { useState, useEffect } from "react";
import ComplaintDetail from './ComplaintDetail';
import MediaPopup from './MediaPopup'; 
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

function StatisticsTable() {
    const [data, setData] = useState([]);
    const [selectedComplaint, setSelectedComplaint] = useState(null);
    const [selectedMedia, setSelectedMedia] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate(); // Get the navigate function

    useEffect(() => {
        const fetchComplaints = async () => {
            try {
                // Get the token from localStorage
                const token = localStorage.getItem('authToken');

                if (!token) {
                    navigate('/login'); // Redirect to login page if no token
                    return;
                }

                const response = await fetch('http://127.0.0.1:8000/get_complaints/', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const complaints = await response.json();
                    setData(complaints);
                } else if (response.status === 401) {
                    setErrorMessage('Unauthorized access. Please log in.');
                    navigate('/login'); // Redirect to login page if unauthorized
                } else {
                    throw new Error('Error fetching complaints.');
                }
            } catch (error) {
                console.error('Error fetching complaints:', error);
                setErrorMessage('Error fetching complaints. Please try again.');
            }
        };

        fetchComplaints();
    }, [navigate]);

    const handleRowClick = (complaint) => {
        setSelectedComplaint(complaint);  // Set the clicked complaint as the selected one
    };

    const handleCloseDetail = () => {
        setSelectedComplaint(null);  // Close the detail view
    };

    const handleShowMedia = (image) => {
        setSelectedMedia(image);  // Show the media in a popup
    };

    const handleCloseMedia = () => {
        setSelectedMedia(null);  // Close the media popup
    };

    const renderComplaintText = (text) => {
        if (typeof text !== 'string') {
            return 'No description available'; // Return a fallback string if text is not a string
        }
        
        return text.length > 20 ? text.substring(0, 20) + '...' : text;
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
            {errorMessage && <p className="text-danger text-center mt-3">{errorMessage}</p>}
            <div className="table-responsive-sm" style={{ flex: 1, overflowY: 'auto' }}>
                <table className="table table-hover" style={{ width: '100%' }}>
                    <thead>
                        <tr className="table-success">
                            <th>SL NO</th>
                            <th>PNR</th>
                            <th>Customer's Complaint</th>
                            <th>Category</th>
                            <th>Sub-Category</th>
                            <th>Priority</th>
                            <th>Status</th>
                            <th>Media</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((complaint, index) => (
                            <tr key={index} onClick={() => handleRowClick(complaint)} style={{ cursor: 'pointer' }}>
                                <td>{index + 1}</td>
                                <td>{complaint.pnr}</td>
                                <td>{renderComplaintText(complaint.description)}</td>
                                <td>{complaint.category}</td>
                                <td>{complaint.sub_category}</td>
                                <td>{complaint.priority}</td>
                                <td>{complaint.status}</td>
                                <td>
                                    <button onClick={(e) => { e.stopPropagation(); handleShowMedia(complaint.image); }} className="btn btn-link">
                                        Show Media
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Render the complaint detail view when a complaint is selected */}
            {selectedComplaint && (
                <ComplaintDetail complaint={selectedComplaint} onClose={handleCloseDetail} />
            )}

            {/* Render the media popup when an image is selected */}
            {selectedMedia && (
                <MediaPopup selectedMedia={selectedMedia} handleCloseMedia={handleCloseMedia} />
            )}
        </div>
    );
}

export default StatisticsTable;
