import React, { useState, useEffect } from "react";
import complaintsData from './Complaints.json';
import ComplaintDetail from './ComplaintDetail';
import MediaPopup from './MediaPopup';

function StatisticsTable() {
    const [data, setData] = useState([]);
    const [selectedComplaint, setSelectedComplaint] = useState(null);
    const [selectedMedia, setSelectedMedia] = useState(null);
    const [statusModalVisible, setStatusModalVisible] = useState(false);
    const [selectedStatus, setSelectedStatus] = useState("");
    const [currentComplaintId, setCurrentComplaintId] = useState(null);

    useEffect(() => {
        setData(complaintsData);
    }, []);

    const handleRowClick = (complaint) => {
        setSelectedComplaint(complaint);
    };

    const handleCloseDetail = () => {
        setSelectedComplaint(null);
    };

    const handleShowMedia = (image) => {
        setSelectedMedia(image);
    };

    const handleCloseMedia = () => {
        setSelectedMedia(null);
    };

    const renderComplaintText = (text) => {
        return text.length > 20 ? text.substring(0, 20) + '...' : text;
    };

    const handleStatusChange = () => {
        setData((prevData) =>
            prevData.map((complaint) =>
                complaint.id === currentComplaintId ? { ...complaint, status: selectedStatus } : complaint
            )
        );
        setStatusModalVisible(false);
        setCurrentComplaintId(null);
        setSelectedStatus("");
    };

    const openStatusModal = (id, currentStatus) => {
        setCurrentComplaintId(id);
        setSelectedStatus(currentStatus);
        setStatusModalVisible(true);
    };

    return (
        <>
            <div className="table-responsive-sm" style={{ overflowX: 'auto', overflowY: 'auto', maxHeight: '100px', maxWidth: '100%' }}>
                <table className="table table-hover">
                    <thead>
                        <tr className="table-success">
                            <th>SL NO</th>
                            <th>PNR</th>
                            <th>Customer's Complaint</th>
                            <th>Category</th>
                            <th>Status</th>
                            <th>Media</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((complaint, index) => (
                            <tr key={index} onClick={() => handleRowClick(complaint)} style={{ cursor: 'pointer' }}>
                                <td>{complaint.sl_no}</td>
                                <td>{complaint.pnr}</td>
                                <td>{renderComplaintText(complaint.customer_complaint)}</td>
                                <td>{complaint.category}</td>
                                <td>{complaint.status}</td>
                                <td>
                                    <button onClick={(e) => { e.stopPropagation(); handleShowMedia(complaint.image); }} className="btn btn-link">
                                        Show Media
                                    </button>
                                </td>
                                <td>
                                    <button onClick={(e) => { e.stopPropagation(); openStatusModal(complaint.id, complaint.status); }} className="btn btn-success">
                                        Change Status
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {selectedComplaint && (
                <ComplaintDetail complaint={selectedComplaint} onClose={handleCloseDetail} />
            )}

            {selectedMedia && (
                <MediaPopup selectedMedia={selectedMedia} handleCloseMedia={handleCloseMedia} />
            )}

            {statusModalVisible && (
                <div className="modal fade show" style={{ display: 'block' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Change Status</h5>
                                <button type="button" className="close" style={{ position: 'absolute', right: '15px', top: '15px' }} onClick={() => setStatusModalVisible(false)}>
                                    &times;
                                </button>
                            </div>
                            <div className="modal-body">
                                <label htmlFor="status" style={{ marginRight: '25px' }}>Update Status:</label>
                                <select
                                    id="status"
                                    value={selectedStatus}
                                    onChange={(e) => setSelectedStatus(e.target.value)}                                    
                                >
                                    <option value="Pending">Pending</option>
                                    <option value="Completed">Completed</option>
                                </select>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-success" onClick={() => setStatusModalVisible(false)}>Cancel</button>
                                <button type="button" className="btn btn-success" onClick={handleStatusChange}>Save changes</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
}

export default StatisticsTable;
