import React, { useState } from 'react';
import axios from 'axios';

function FeedbackForm() {
  const [pnr, setPnr] = useState('');
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');

  const handlePnrChange = (event) => {
    setPnr(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleSubmit = async () => {
    if (!pnr || !file || !description) {
      setMessage('Please fill in all the fields');
      return;
    }

    // Create the complaint object to match the schema on the backend
    const complaintData = {
      pnr: pnr,
      description: description
    };

    // Prepare the form data with the image file and complaint JSON string
    const formData = new FormData();
    formData.append('image', file);  // Add the image file
    formData.append('complaint', JSON.stringify(complaintData));  // Add the complaint data as JSON string

    try {
      // Post the form data to the backend
      const response = await axios.post(
        'http://127.0.0.1:8000/add_complaint_with_image/', 
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      // Handle the response
      setMessage('Complaint submitted successfully');
      console.log(response.data);
    } catch (error) {
      setMessage('Error submitting the complaint');
      console.error(error);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center min-vh-100">
      <div className="card shadow" style={{ width: '400px' }}>
        <div className="card-body">
          <h2 className="card-title text-center">Complaint Enquiry</h2>
          <div className="form-group">
            <label htmlFor="pnr">Passenger Name Record (PNR):</label>
            <input type="text" className="form-control" id="pnr" value={pnr} onChange={handlePnrChange} maxLength={10} />
          </div>
          <div className="form-group">
            <label htmlFor="file">Upload File:</label>
            <input type="file" className="form-control" id="file" onChange={handleFileChange} />
          </div>
          <div className="form-group">
            <label htmlFor="description">Complaint Description:</label>
            <textarea className="form-control" id="description" value={description} onChange={handleDescriptionChange} rows="3"></textarea>
          </div>
          <div className="text-center mt-3">
            <button className="btn btn-success" onClick={handleSubmit}>Submit</button>
          </div>
          {message && <p className="text-center mt-2">{message}</p>}
        </div>
      </div>
    </div>
  );
}

export default FeedbackForm;
