import React, { useState, useRef } from 'react';
import axios from 'axios';
import Notification from './Notification'; // Assuming Notification is in the same folder
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap

function Complaint() {
  const [pnr, setPnr] = useState('');
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [pnrValid, setPnrValid] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const fileInputRef = useRef(null);

  // PNR validation
  const validatePnr = (pnr) => {
    const pnrRegex = /^[1-9]{3}-\d{7}$/;
    return pnrRegex.test(pnr);
  };

  const handlePnrChange = (event) => {
    const newPnr = event.target.value;
    const allowedCharacters = /^[0-9-]*$/;

    if (newPnr.length <= 11 && allowedCharacters.test(newPnr)) {
      setPnr(newPnr);

      if (validatePnr(newPnr)) {
        setPnrValid(true);
        setErrorMessage('');
      } else {
        setPnrValid(false);
        setErrorMessage('Enter valid PNR');
      }
    }
  };

  const handlePhoneNumberChange = (event) => {
    const newPhoneNumber = event.target.value;
    if (/^\d*$/.test(newPhoneNumber) && newPhoneNumber.length <= 10) {
      setPhoneNumber(newPhoneNumber);
    }
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  // Send OTP function
  const handleOtpClick = async () => {
    if (!phoneNumber || phoneNumber.length !== 10) {
      setErrorMessage('Please enter a valid 10-digit phone number.');
    } else {
      try {
        console.log('Sending OTP to:', phoneNumber);
        setOtpSent(true);
        setErrorMessage('OTP sent successfully');
      } catch (error) {
        setErrorMessage('Error sending OTP');
        console.error(error);
      }
    }
  };

  const handleOtpChange = (event) => {
    const newOtp = event.target.value;
    if (/^\d{0,4}$/.test(newOtp)) {
      setOtp(newOtp);
    }
  };

  // Verify OTP
  const handleOtpVerify = () => {
    if (otp === '1234') {
      setOtpVerified(true);
      setErrorMessage('');
      console.log('OTP Verified');
    } else {
      setOtpVerified(false);
      setErrorMessage('Invalid OTP. Please try again.');
    }
  };

  // Form submission
  const handleSubmit = async () => {
    if (!pnr || !pnrValid || !file || !description || !phoneNumber || !otpVerified) {
      setErrorMessage('Please complete all fields with valid inputs before submitting.');
      return;
    }

    setIsSubmitting(true);

    const formData = new FormData();
    formData.append('image', file);
    formData.append('pnr', pnr);
    formData.append('description', description);

    try {
      const response = await axios.post('http://127.0.0.1:8000/add_complaint_with_image/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setShowNotification(true);
      setPnr('');
      setFile(null);
      setDescription('');
      setPhoneNumber('');
      setOtp('');
      setOtpVerified(false);

      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

      console.log(response.data);
    } catch (error) {
      setErrorMessage('Error submitting the complaint');
      console.error(error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCloseNotification = () => {
    setShowNotification(false);
  };

  return (
    <div className="container d-flex justify-content-center align-items-center min-vh-100">
      <div className="card shadow" style={{ width: '500px' }}> {/* Set fixed width */}
        <div className="card-body">
          <h2 className="card-title text-center">Complaint Enquiry</h2>

          {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

          <div className="mb-3">
            <label htmlFor="pnr" className="form-label">Passenger Name Record (PNR):</label>
            <input
              type="text"
              className="form-control"
              id="pnr"
              value={pnr}
              onChange={handlePnrChange}
              maxLength={11}
              placeholder="Enter PNR (e.g., 123-4567890)"
            />
          </div>

          <div className="mb-3 row">
            <div className="col-8">
              <label htmlFor="phoneNumber" className="form-label">Phone Number:</label>
              <input
                type="text"
                className="form-control"
                id="phoneNumber"
                value={phoneNumber}
                onChange={handlePhoneNumberChange}
                maxLength={10}
              />
            </div>
            <div className="col-4 d-flex align-items-end">
              <button
                className="btn btn-primary"
                onClick={handleOtpClick}
                disabled={otpSent}
              >
                {otpSent ? 'OTP Sent' : 'Send OTP'}
              </button>
            </div>
          </div>

          {otpSent && (
            <div className="mb-3 row">
              <div className="col-8">
                <label htmlFor="otp" className="form-label">Enter OTP:</label>
                <input
                  type="text"
                  className="form-control"
                  id="otp"
                  value={otp}
                  onChange={handleOtpChange}
                  maxLength={4}
                />
              </div>
              <div className="col-4 d-flex align-items-end">
                <button className="btn btn-secondary" onClick={handleOtpVerify}>
                  Verify OTP
                </button>
              </div>
            </div>
          )}

          <div className="mb-3">
            <label htmlFor="file" className="form-label">Upload File:</label>
            <input
              type="file"
              className="form-control"
              id="file"
              onChange={handleFileChange}
              ref={fileInputRef}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="description" className="form-label">Complaint Description:</label>
            <textarea
              className="form-control"
              id="description"
              value={description}
              onChange={handleDescriptionChange}
              rows="3"
            />
          </div>

          <div className="text-center mt-3">
            <button
              className="btn btn-success"
              onClick={handleSubmit}
              disabled={isSubmitting || !otpVerified}
            >
              {isSubmitting ? 'Submitting...' : 'Submit'}
            </button>
          </div>
        </div>
      </div>

      {showNotification && <Notification onClose={handleCloseNotification} />}
    </div>
  );
}

export default Complaint;
