import React, { useState, useRef } from 'react';
import axios from 'axios';
import Notification from './Notification'; // Import the Notification component
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap

function Complaint() {
  const [pnr, setPnr] = useState('');
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const fileInputRef = useRef(null);

  const handlePnrChange = (event) => setPnr(event.target.value);
  const handleFileChange = (event) => setFile(event.target.files[0]);
  const handleDescriptionChange = (event) => setDescription(event.target.value);
  const handlePhoneNumberChange = (event) => setPhoneNumber(event.target.value);
  const handleOtpChange = (event) => setOtp(event.target.value);


  
  // Send OTP 
  const handleSendOtp = async () => {
  if (phoneNumber.length !== 10) {
    setErrorMessage('Please enter a valid 10-digit phone number.');
    return;
  }

  try {
    await axios.post(`http://127.0.0.1:8000/send_otp/${phoneNumber}`);
    setOtpSent(true);
    setErrorMessage('');
  } catch (error) {
    setErrorMessage('Error sending OTP');
    console.error(error);
  }
};


  // Verify OTP function
  const handleVerifyOtp = async () => {
    try {
      await axios.post(`http://127.0.0.1:8000/verify_otp/${phoneNumber}&&${otp}`);
      setOtpVerified(true);
      setErrorMessage('');
      console.log('OTP Verified');
    } catch (error) {
      setOtpVerified(false);
      setErrorMessage('Invalid OTP. Please try again.');
      console.error(error);
    }
  };

  const handleSubmit = async () => {
    if (!pnr || !file || !description || !otpVerified) {
      setErrorMessage('Please complete all fields with valid inputs before submitting.');
      return;
    }

    setIsSubmitting(true);

    const formData = new FormData();
    formData.append('image', file);
    formData.append('pnr', pnr);
    formData.append('description', description);

    try {
      const response = await axios.post('http://127.0.0.1:8000/add_complaint_with_image/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setShowNotification(true);
      setPnr('');
      setFile(null);
      setDescription('');
      setPhoneNumber('');
      setOtp('');
      setOtpVerified(false);

      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

      console.log(response.data);
    } catch (error) {
      setErrorMessage('Error submitting the complaint');
      console.error(error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCloseNotification = () => setShowNotification(false);

  return (
    <div className="container d-flex justify-content-center align-items-center min-vh-100">
      <div style={{ paddingBottom: '900px' }}></div> {/* Added padding-bottom for alignment */}
      <div className="card w-50 shadow">
        <div className="card-body">
          <h2 className="card-title text-center">Complaint Enquiry</h2>

          {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

          <div className="mb-3">
            <label htmlFor="pnr" className="form-label">Passenger Name Record (PNR):</label>
            <input
              type="text"
              className="form-control"
              id="pnr"
              value={pnr}
              onChange={handlePnrChange}
              maxLength={11}
              placeholder="Enter PNR (e.g., 123-4567890)"
            />
          </div>

          <div className="mb-3 d-flex align-items-center">
            <div className="me-2 flex-grow-1">
              <label htmlFor="phoneNumber" className="form-label">Phone Number:</label>
              <input
                type="text"
                className="form-control"
                id="phoneNumber"
                value={phoneNumber}
                onChange={handlePhoneNumberChange}
                maxLength={10}
              />
            </div>
            <button
              className="btn btn-primary"
              onClick={handleSendOtp}
              disabled={otpSent}
            >
              {otpSent ? 'OTP Sent' : 'Send OTP'}
            </button>
          </div>

          {otpSent && (
            <div className="mb-3 d-flex align-items-center">
              <div className="me-2 flex-grow-1">
                <label htmlFor="otp" className="form-label">Enter OTP:</label>
                <input
                  type="text"
                  className="form-control"
                  id="otp"
                  value={otp}
                  onChange={handleOtpChange}
                  maxLength={4}
                />
              </div>
              <button
                className="btn btn-secondary"
                onClick={handleVerifyOtp}
              >
                Verify OTP
              </button>
            </div>
          )}

          <div className="mb-3">
            <label htmlFor="file" className="form-label">Upload File:</label>
            <input
              type="file"
              className="form-control"
              id="file"
              onChange={handleFileChange}
              ref={fileInputRef}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="description" className="form-label">Complaint Description:</label>
            <textarea
              className="form-control"
              id="description"
              value={description}
              onChange={handleDescriptionChange}
              rows="3"
            ></textarea>
          </div>

          <div className="text-center mt-3">
            <button
              className="btn btn-success"
              onClick={handleSubmit}
              disabled={isSubmitting || !otpVerified}
            >
              {isSubmitting ? 'Submitting...' : 'Submit'}
            </button>
          </div>
        </div>
      </div>

      {showNotification && <Notification onClose={handleCloseNotification} />}
    </div>
  );
}

export default Complaint;
